import{_ as r,c as a,o,a1 as e}from"./chunks/framework.CPsxJxQM.js";const c=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"大语言模型/RAG/index.md","filePath":"大语言模型/RAG/index.md"}'),n={name:"大语言模型/RAG/index.md"};function l(s,t,i,d,g,h){return o(),a("div",null,t[0]||(t[0]=[e('<h3 id="📚-想象一个开卷考试的学生" tabindex="-1">📚 <strong>想象一个开卷考试的学生</strong> <a class="header-anchor" href="#📚-想象一个开卷考试的学生" aria-label="Permalink to &quot;📚 **想象一个开卷考试的学生**&quot;">​</a></h3><ol><li><strong>普通大模型</strong>：像闭卷考试，只能靠自己的记忆（训练数据）回答问题，不知道最新或特定资料。</li><li><strong>RAG大模型</strong>：像开卷考试，遇到问题时，先<strong>快速查阅资料库</strong>（检索），再结合资料和自己的知识（生成）写出答案。</li></ol><h3 id="🔍-rag-三步流程" tabindex="-1">🔍 <strong>RAG 三步流程</strong> <a class="header-anchor" href="#🔍-rag-三步流程" aria-label="Permalink to &quot;🔍 **RAG 三步流程**&quot;">​</a></h3><ol><li><p><strong>检索（Retrieve）</strong></p><ul><li>用户提问后，系统从<strong>外部数据库</strong>（如文档、网页）中搜索相关片段。<br><em>例子：问“如何保养自行车？” → 检索到《自行车手册》中的保养章节。</em></li></ul></li><li><p><strong>增强（Augment）</strong></p><ul><li>把检索到的资料和用户问题一起喂给大模型，作为背景信息。</li></ul></li><li><p><strong>生成（Generate）</strong></p><ul><li>大模型结合检索到的资料+自身知识，生成<strong>准确且有针对性的答案</strong>。<br><em>输出：“根据《自行车手册》，建议每月链条上油，胎压保持…”</em></li></ul></li></ol><hr><h3 id="🌟-rag-的好处" tabindex="-1">🌟 <strong>RAG 的好处</strong> <a class="header-anchor" href="#🌟-rag-的好处" aria-label="Permalink to &quot;🌟 **RAG 的好处**&quot;">​</a></h3><ul><li><strong>解决大模型“幻觉”</strong>：避免瞎编，答案有依据。</li><li><strong>动态更新知识</strong>：无需重新训练模型，更新数据库即可（比如最新新闻）。</li><li><strong>专业化</strong>：可连接企业文档、法律条文等特定资料库。</li></ul><hr><h3 id="🆚-对比传统大模型" tabindex="-1">🆚 <strong>对比传统大模型</strong> <a class="header-anchor" href="#🆚-对比传统大模型" aria-label="Permalink to &quot;🆚 **对比传统大模型**&quot;">​</a></h3><table tabindex="0"><thead><tr><th>场景</th><th>传统大模型</th><th>RAG</th></tr></thead><tbody><tr><td>问“2025年奥运会举办地？”</td><td>可能答错（训练数据截止2023年）</td><td>检索最新网页，答“巴黎”</td></tr><tr><td>问“我司产品的定价？”</td><td>无法回答</td><td>检索内部文档，生成准确答案</td></tr></tbody></table><hr><p>总结：<strong>RAG = 搜索引擎 + 大模型</strong>，让AI的回答更精准、更及时！ 🚀</p>',12)]))}const _=r(n,[["render",l]]);export{c as __pageData,_ as default};
