item.description:
安装 brew install mysql@5.7 https://downloads.mysql.com/archives/community/
启动 sudo /usr/local/mysql-5.6.10-osx10.7-x86_64/bin/mysqld &ndash;user=mysql
初始登陆 mysql -u root -p
重置密码 root用户密码重置为root123 set password for root@localhost = password(&lsquo;root123&rsquo;);
创建数据库 colorful create database colorful charset=utf8 ;
进入数据库 colorful use colorful;
在库 colorful 内创建一张表 c_test CREATE TABLE c_test ( id bigint(20) NOT NULL AUTO_INCREMENT COMMENT &lsquo;主键&rsquo;, name varchar(100) NOT NULL COMMENT &lsquo;姓名&rsquo;, age int DEFAULT 1 COMMENT &lsquo;年龄&rsquo;, PRIMARY KEY (id), KEY idx_name (name) ) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8 COMMENT=&lsquo;测试表&rsquo;
